import React, { Component } from 'react'
import './NoteForm.css'

class NoteForm extends Component {
    constructor(props){
        super(props);
        this.state = {
            newNoteContent:'',
        };
    }

    handleUserInput = (e) => {
        this.setState({
            newNoteContent : e.target.value,
        })
    }


    writeNote = () => {

        this.setState({
            newNoteContent: '',
        })
    }

    render() { 
        return ( 
            <div className="formWrapper">
                <input type="text" className="noteInput" placeholder="Write a new note..." value={this.state.newNoteContent} onChange={this.handleUserInput}/>
                <button className="noteButton">Add Note</button>
            </div>
         );
    }
}

export default NoteForm;
